CREATE FUNCTION docs.get_alg_saldo_kpv(l_alg_saldo_kpv date, l_doc_kpv date, l_kpv1 date, l_kpv2 date) RETURNS date
    LANGUAGE plpgsql
    AS $$
DECLARE
    l_kpv DATE = l_doc_kpv;
BEGIN
    -- нач сальдо
    IF (l_kpv1 > l_doc_kpv)
    THEN
        l_kpv = l_doc_kpv;
    ELSIF
        l_kpv1 = l_doc_kpv
    THEN
-- дата проводки совпадает с датой нач. периода запроса. напр 01.01
        l_kpv = coalesce(l_alg_saldo_kpv, l_doc_kpv);
    ELSIF
        l_kpv1 < l_doc_kpv AND l_kpv2 >= coalesce(l_alg_saldo_kpv, l_doc_kpv)
    THEN
        -- конец периода на дату сальдо (напр. 31.12)
        l_kpv = l_doc_kpv;
    ELSE
        l_kpv = l_doc_kpv;
    END IF;
    RETURN l_kpv;
END;
$$;


