           AND ((l_params ->> 'uritus') IS NULL OR
                coalesce(j1.kood4, '') ILIKE coalesce((l_params ->> 'uritus'), '') || '%')

         UNION ALL
         SELECT d.rekvid,
                0 :: NUMERIC(14, 2)           AS alg_saldo,
                0 :: NUMERIC                  AS deebet,
                (j1.summa)                    AS kreedit,
                trim(j1.kreedit)::VARCHAR(20) AS konto
         FROM docs.doc d
