CREATE FUNCTION docs.loe_makse(user_id integer, l_id integer, OUT error_code integer, OUT result integer, OUT error_message text, OUT data jsonb) RETURNS record
    LANGUAGE plpgsql
    AS $$
DECLARE
    l_mk_id       INTEGER;
