    arv_history = row_to_json(row.*)
                  FROM (SELECT a.*
                        FROM docs.arv a
                        WHERE a.parentid = doc_id) ROW;

    -- docs.arv1

    arv1_history = jsonb_build_array(array(SELECT row_to_json(row.*)
                                           FROM (SELECT a1.*
                                                 FROM docs.arv1 a1
                                                          INNER JOIN docs.arv a ON a.id = a1.parentid
                                                 WHERE a.parentid = doc_id) row));
    -- docs.arvtasu

