    new_history   JSONB;
    l_props       JSONB;
    l_string      TEXT;
    l_kasutaja    TEXT;
    l_doc_type_id INTEGER = (SELECT id
                             FROM libs.library
                             WHERE library = 'DOK'
                               AND kood = 'TAOTLUS_LOGIN'
                             LIMIT 1);
BEGIN

