                                 ) row);

    roles_json   JSONB   = (SELECT to_jsonb(row)
                            FROM (SELECT coalesce((doc_data ->> 'is_kasutaja') :: BOOLEAN, FALSE)    AS is_kasutaja,
                                         coalesce((doc_data ->> 'is_admin') :: BOOLEAN, FALSE)       AS is_admin,
