    ELSE

        SELECT row_to_json(row)
        INTO new_history
        FROM (SELECT now()    AS updated,
                     userName AS user,
                     u.kasutaja,
                     u.properties,
                     u.roles,
                     u.ametnik
              FROM ou.userid u
              WHERE u.id = doc_id) row;


        UPDATE ou.userid
        SET ametnik    = doc_ametnik,
            roles      = roles_json,
            muud       = doc_muud,
            properties = props_json,
            ajalugu    = new_history
        WHERE id = doc_id RETURNING id
            INTO new_user_id;
    END IF;


    RETURN new_user_id;

END;
$$;


