            WHERE m.kpv > l_kpv
              AND ltrim(rtrim(m1.tunnus)) = l_lib_kood
            UNION
            SELECT DISTINCT 'Dok PV operatsioon inv.nr.:' || ltrim(rtrim(p.kood)) || ' (' || ltrim(rtrim(r.nimetus)) ||
