            WHERE o.kpv > l_kpv
              AND ltrim(rtrim(o.tunnus)) = l_lib_kood


 */
            UNION ALL
            SELECT DISTINCT 'Dok lausend nr.:' || ltrim(rtrim(m.number::TEXT)) || ' (' || ltrim(rtrim(r.nimetus)) ||
